var parallaxContainer=document.getElementById("parallax"),layers=parallaxContainer.children;window.addEventListener("mousemove",function(n){var a=n.pageX,e=n.pageY,r=window.innerWidth/2-a,t=window.innerHeight/2-e;[].slice.call(layers).forEach(function(n,a){var e=a/100,i=window.innerHeight/2*e,l=r*e,o=t*e,d=n.style,p="translate3d("+l+"px,"+o+"px, 0)";d.transform=p,d.bottom="-"+i+"px"})});